import axios from 'axios'
import config from 'config'

const categoryAPI = {
  getStoresByCategory ({ categoryName }) {
    let path = `${config.ApiDomain}/api/category?slug=${categoryName}`
    return axios.get(path).then((res, err) => {
      return res
    })
  }
}

export default categoryAPI
