import categoryAPI from 'libs/category'

export const GET_STORES_BY_CATEGORY = Symbol('GET_STORES_BY_CATEGORY')
export const GET_STORES_BY_CATEGORY_SUCCESS = Symbol('GET_STORES_BY_CATEGORY_SUCCESS')
export const GET_STORES_BY_CATEGORY_FAIL = Symbol('GET_STORES_BY_CATEGORY_FAIL')

export function getStoresByCategory (categoryName) {
  return dispatch => {
    dispatch({
      type: GET_STORES_BY_CATEGORY
    })
    return categoryAPI.getStoresByCategory(categoryName).then(res => {
      dispatch(onGetStoresByCategory(res))
    })
  }
}

function onGetStoresByCategory (payload) {
  const { status } = payload
  const type = status === 404 ? GET_STORES_BY_CATEGORY_FAIL : GET_STORES_BY_CATEGORY_SUCCESS
  return {
    type,
    payload
  }
}
