import storeAPI from 'libs/store'

export const GET_STORES = Symbol('GET_STORES')
export const GET_STORES_SUCCESS = Symbol('GET_STORES_SUCCESS')
export const GET_STORES_FAIL = Symbol('GET_STORES_FAIL')

export function getStores () {
  return dispatch => {
    dispatch({
      type: GET_STORES
    })

    return storeAPI.getStores().then(res => {
      dispatch(onGetStores(res))
    })
  }
}

function onGetStores (payload) {
  const { status } = payload
  const type = status === 404 ? GET_STORES_FAIL : GET_STORES_SUCCESS
  return {
    type,
    payload
  }
}
