import Immutable from 'immutable'
import * as ActionType from 'actions/merchant'

export const initialState = Immutable.fromJS({
  isLoading: false
})

export default function (state = initialState, action) {
  switch (action.type) {
    case ActionType.GET_MERCHANT_COUPONS:
      return state.set('isLoading', true)

    case ActionType.GET_MERCHANT_COUPONS_SUCCESS:
      return state.merge(
        Object.assign({}, action.payload.data, {
          isLoading: false,
          status: 200
        })
      )
    case ActionType.GET_MERCHANT_COUPONS_FAIL:
      return {
        ...state,
        status: 404
      }

    default:
      return state
  }
}
