import axios from 'axios'
import config from 'config'

const merchantAPI = {
  getMerchantCoupons ({ merchantSlug }) {
    let path = `${config.ApiDomain}/store?slug=${merchantSlug}`
    return axios.get(path).then((res, err) => {
      return res.data
    })
  }
}

export default merchantAPI
