import categoriesAPI from 'libs/categories'

export const GET_CATEGORIES = Symbol('GET_CATEGORIES')
export const GET_CATEGORIES_SUCCESS = Symbol('GET_CATEGORIES_SUCCESS')
export const GET_CATEGORIES_FAIL = Symbol('GET_CATEGORIES_FAIL')

export function getCategories () {
  return dispatch => {
    dispatch({
      type: GET_CATEGORIES
    })

    return categoriesAPI.getCategories().then(res => {
      dispatch(onGetCategories(res))
    })
  }
}

function onGetCategories (payload) {
  const { status } = payload
  const type = status === 404 ? GET_CATEGORIES_FAIL : GET_CATEGORIES_SUCCESS
  return {
    type,
    payload
  }
}
