import React, { PureComponent } from 'react'
import styled from 'styled-components'
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
// import { faSearch } from '@fortawesome/fontawesome-free-solid'
import Footer from '/components/Footer/Footer'
import Header from '/components/Header/Header'
import StickyInstallHeader from '/components/Header/StickyInstallHeader'
const LayoutContainer = styled.div`
  background: #fafafa;
`
const ContainerContent = styled.div`
  padding-top: 100px;
  @media (max-width: 1000px) {
    padding: 70px 0 0 0;
    max-width: 100%;
  }
`


export default class Layout extends PureComponent {
  render () {
    const { isDesktop, userToken } = this.props
    return (
      <LayoutContainer className='layout'>
        <Header userToken={userToken} />
        {isDesktop && <StickyInstallHeader />}
        <ContainerContent className='container main-container'>
          {this.props.children}
        </ContainerContent>
        <Footer />
      </LayoutContainer>
    )
  }
}
