import withRedux from 'next-redux-wrapper'
import { ThemeProvider, createGlobalStyle } from 'styled-components'
import styledNormalize from 'styled-normalize'
import { withRouter } from 'next/router'
import { Provider } from 'react-redux'
import App from 'next/app'
import Layout from 'components/Layout'
import createStore from 'store/createStore'
import 'bootstrap.scss'
import 'globalStyle.scss'
import theme from 'theme'
import TagManager from 'react-gtm-module'
import MobileDetect from 'mobile-detect'
import { auth } from '../utils/auth'

const tagManagerArgs = {
  gtmId: 'GTM-KVH7L9'
}

const GlobalStyle = createGlobalStyle`
  ${styledNormalize}
`

class MyApp extends App {
  static async getInitialProps ({ Component, ctx }) {
    const { req } = ctx
    const allToken = auth(ctx)
    const token = !!allToken.token
    const userToken = allToken.userToken
    const userAgent = process.browser
      ? window.navigator.userAgent
      : req.headers['user-agent']
    const md = new MobileDetect(userAgent)
    const isDesktop = md.mobile() === null
    return {
      token,
      userToken,
      isDesktop,
      pageProps: Component.getInitialProps
        ? await Component.getInitialProps(ctx)
        : {}
    }
  }
  componentDidMount () {
    TagManager.initialize(tagManagerArgs)
  }
  render () {
    const {
      Component,
      pageProps,
      store,
      router,
      isDesktop,
      token,
      userToken
    } = this.props
    return (
      <ThemeProvider theme={theme}>
        <Provider store={store}>
          <GlobalStyle />
          <Layout isDesktop={isDesktop} userToken={userToken}>
            <Component
              router={router}
              {...pageProps}
              token={token}
              userToken={userToken}
              isDesktop={isDesktop}
            />
          </Layout>
        </Provider>
      </ThemeProvider>
    )
  }
}

export default withRedux(createStore)(withRouter(MyApp))
