import merchantAPI from 'libs/merchant'

export const GET_MERCHANT_COUPONS = Symbol('GET_MERCHANT_COUPONS')
export const GET_MERCHANT_COUPONS_SUCCESS = Symbol('GET_MERCHANT_COUPONS_SUCCESS')
export const GET_MERCHANT_COUPONS_FAIL = Symbol('GET_MERCHANT_COUPONS_FAIL')

export function getMerchantCoupons (merchantSlug) {
  return dispatch => {
    dispatch({
      type: GET_MERCHANT_COUPONS
    })

    return merchantAPI.getMerchantCoupons(merchantSlug).then(res => {
      dispatch(onGetMerchantCoupons(res))
    })
  }
}

function onGetMerchantCoupons (payload) {
  const { status } = payload
  const type = status === 404 ? GET_MERCHANT_COUPONS_FAIL : GET_MERCHANT_COUPONS_SUCCESS
  return {
    type,
    payload
  }
}
